DROP TABLE IF EXISTS `orders`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `orders` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `user_id` bigint NOT NULL COMMENT 'User ID',
  `distributor_id` bigint DEFAULT NULL COMMENT 'Distributor ID (from passcode)',
  `source_passcode_id` bigint DEFAULT NULL COMMENT 'Source Passcode ID',
  `source_book_id` bigint DEFAULT NULL COMMENT 'Source Book ID (from which book)',
  `source_entry` varchar(50) DEFAULT NULL COMMENT 'Source Entry: profile, reader',
  `order_no` varchar(100) NOT NULL COMMENT 'Order Number',
  `amount` decimal(10,2) NOT NULL COMMENT 'Amount',
  `status` varchar(20) NOT NULL COMMENT 'Status: Pending/Paid/Refunded',
  `platform` varchar(20) DEFAULT NULL COMMENT 'Platform: AppStore/GooglePay',
  `product_id` varchar(100) DEFAULT NULL COMMENT 'Product ID',
  `order_type` varchar(20) DEFAULT 'onetime' COMMENT 'Order Type: onetime, subscription',
  `subscription_period` varchar(20) DEFAULT NULL COMMENT 'Subscription Period: monthly, quarterly, yearly',
  `subscription_start_date` datetime DEFAULT NULL COMMENT 'Subscription Start Date',
  `subscription_end_date` datetime DEFAULT NULL COMMENT 'Subscription End Date',
  `is_auto_renew` tinyint(1) DEFAULT '1' COMMENT 'Auto Renew: 0-No, 1-Yes',
  `cancel_date` datetime DEFAULT NULL COMMENT 'Cancel Date',
  `cancel_reason` varchar(255) DEFAULT NULL COMMENT 'Cancel Reason',
  `original_transaction_id` varchar(255) DEFAULT NULL COMMENT 'Original Transaction ID (Apple/Google)',
  `platform_transaction_id` varchar(255) DEFAULT NULL COMMENT 'Platform Transaction ID',
  `purchase_token` text COMMENT 'Purchase Token (Google)',
  `receipt_data` text COMMENT 'Receipt Data (Apple)',
  `verified_at` datetime DEFAULT NULL COMMENT '验证时间',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP,
  `update_time` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_order_no` (`order_no`),
  KEY `idx_distributor_id` (`distributor_id`),
  KEY `idx_source_passcode_id` (`source_passcode_id`),
  KEY `idx_order_type` (`order_type`),
  KEY `idx_subscription_end_date` (`subscription_end_date`),
  KEY `idx_original_transaction_id` (`original_transaction_id`),
  KEY `idx_source_book_id` (`source_book_id`),
  KEY `idx_user_id_status` (`user_id`,`status`),
  KEY `idx_create_time` (`create_time`),
  KEY `idx_verified_at` (`verified_at`)
) ENGINE=InnoDB AUTO_INCREMENT=38 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='Order Table - Commission is calculated dynamically: amount * 0.30 for orders with distributor_id';
